function ConvertTo-Hashtable { 
    [CmdletBinding()] 
    [OutputType('hashtable')] 
    param ( [Parameter(ValueFromPipeline)] $InputObject )
        process { 
        if ($null -eq $InputObject) { return $null; } 
        if ($InputObject -is [System.Collections.IEnumerable] -and $InputObject -isnot [string]) { 
            $collection = @( foreach ($object in $InputObject) { ConvertTo-Hashtable -InputObject $object; } ); 
            Write-Output -NoEnumerate $collection 
        } elseif ($InputObject -is [psobject]) { 
            $hash = @{}; foreach ($property in $InputObject.PSObject.Properties) { $hash[$property.Name] = ConvertTo-Hashtable -InputObject $property.Value; } $hash; 
        } else { $InputObject; } 
    } 
} 

$tzarr = $tzstring | ConvertFrom-Json | ConvertTo-HashTable; 
$tz = tzutil /g; 
$linuxtz = ""; 
foreach( $tzpair in $tzarr ) { 
    if ( $tzpair.Get_Item("Windows") -eq $tz ) { 
        $linuxtz = $tzpair.Get_Item("Linux"); break; 
    } 
} 
Write-Host $linuxtz; 